import random
import sys


def generate_random_prompt(subject, actions, places, styles, formats, boosters, perspectives, views, artists, mediums, environments, lights, colors, moods):
    text = []
    
    # Step 1: Subject
    text.append(subject)
    
    # Step 2: Action
    if random.random() < 0.5:
        action = random.choice(actions)
        text.append(action)
    
    # Step 3: Place
    if random.random() < 0.5:
        place = random.choice(places)
        text.append(place)
    
    # Step 4: Style
    num_styles = random.randint(0, 2)
    styles = random.sample(styles, num_styles)
    text.extend(styles)
    
    # Step 5: Format
    num_formats = random.randint(0, 2)
    formats = random.sample(formats, num_formats)
    text.extend(formats)
    
    # Step 6: Booster
    num_boosters = random.randint(0, 5)
    boosters = random.sample(boosters, num_boosters)
    text.extend(boosters)
    
    # Step 7: Perspective
    if random.random() < 0.5:
        perspective = random.choice(perspectives)
        text.append(perspective)
    
    # Step 8: View
    if random.random() < 0.5:
        view = random.choice(views)
        text.append(view)
    
    # Step 9: Artists
    num_artists = random.randint(0, 3)
    artists = random.sample(artists, num_artists)
    text.extend(artists)
    
    # Step 10: Medium
    if random.random() < 0.5:
        medium = random.choice(mediums)
        text.append(medium)
    
    # Step 11: Environment
    if random.random() < 0.5:
        environment = random.choice(environments)
        text.append(environment)
    
    # Step 12: Lighting
    if random.random() < 0.5:
        lighting = random.choice(lights)
        text.append(lighting)
    
    # Step 13: Color
    num_colors = random.randint(0, 5)
    colors = random.sample(colors, num_colors)
    text.extend(colors)
    
    # Step 14: Mood
    if random.random() < 0.5:
        mood = random.choice(moods)
        text.append(mood)
    
    return ', '.join(text)


def generate_random_prompts_from_file(file_path, actions, places, styles, formats, boosters, perspectives, views, artists, mediums, environments, lights, colors, moods, n=55):
    with open(file_path, 'r') as file:
        lines = file.readlines()
        
    random.shuffle(lines)
    
    for _ in range(n):
        random.shuffle(lines)
        for line in lines:
            text = line.strip()
            random_prompt = generate_random_prompt(text, actions, places, styles, formats, boosters, perspectives, views, artists, mediums, environments, lights, colors, moods)
            print(random_prompt)

            
